import React, { Component } from 'react'
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {CourseConsumer} from '../Context' 

export default class Product extends Component {
    render() {
        const {id, title, img, price, inCart} = this.props.product;
        return (
            <CourseWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
                <div className="card">
                    <div className="img-container p-5" onClick={() => console.log("You clicked")}>
                       <Link to="/details">
                           <img src={img} alt="product" className="card-img-top"/>
                           </Link> 
                           <button className="card-btn" disabled={inCart?true:false} onClick={() => {console.log("Added to cart")}} >
                            {inCart?(<p className="text-capitalize mb-0" disabled>
                                {" "}
                                in Cart
                            </p>):(<i className="fas fa-cart-plus"/>)}
                        </button>
                    </div>
                    <div className="card-footer d-flex justify-content-between">
                        <p className="align-self-center mb-0">
                            {title}
                        </p>
                        <h5 className="font-italic mb-0">
                            <span className="mr-1">Rs.</span>
                            {price}
                        </h5>
                    </div>
                </div>
            </CourseWrapper>
        )
    }
}


const CourseWrapper = styled.div`

`
